var tab = {
    nav1: null,
    txt1: null,
    init: function() {
        if (tab.nav1 = document.getElementById("tabNavUnit1").getElementsByClassName("tabnav1"), tab.txt1 = document.getElementById("tabContentUnit1").getElementsByClassName("tabtxt1"), 0 == tab.nav1.length || 0 == tab.txt1.length || tab.nav1.length != tab.txt1.length) console.log("ERROR STARTING TABS");
        else {
            for (var t = 0; t < tab.nav1.length; t++) tab.nav1[t].dataset.pos = t, tab.nav1[t].addEventListener("click", tab.switch);
            tab.nav1[0].classList.add("active"), tab.txt1[0].classList.add("active")
        }
    },
    switch: function() {
        for (var t of tab.nav1) t.classList.remove("active");
        for (var t of tab.txt1) t.classList.remove("active");
        tab.nav1[this.dataset.pos].classList.add("active"), tab.txt1[this.dataset.pos].classList.add("active")
    }
};
window.addEventListener("load", tab.init);
